import React, { Component } from "react";

class QuoteControl extends Component {
  constructor(props) {
    super(props);
    this.state = {
      quote: "",
      author: "",
    };
  }
  render() {
    const { onChange } = this.props;
    return (
      <div className="quote-box" style={{ width: "98%" }}>
        <button
          onClick={onChange}
          style={{
            padding: "15px 32px",
            backgroundColor: "#1DA1F2",
            width: "12%",
            height: "100px",
            fontSize: "italic",
            borderRadius: "50em",
            display: "flex",
            alignItems: "center",
          }}
        >
          Click Me
        </button>
        <button onClick={onChange}>Hide Button</button>
      </div>
    );
  }
}

export default QuoteControl;
