import React, { Component } from "react";
import Quote from "./quote";
import QuoteControl from "./quotecontrol";

class QuoteViewer extends Component {
    constructor(props){
    super(props);
    this.state={activeButton:false}
}
  render(){
    let quotes;
    if (this.state.activeButton === true) {
        quotes=(
            <Quote/>
        )
    }
    return (
        <div className ="quote-box">
            <QuoteControl/>
            <button onClick ={this.activeButton} id ="new-quote" style={{}}>Get New Quote</button>
            <h5>{quotes}</h5>
        </div>
    )
  }

}


export default QuoteViewer;
