import React, { Component } from "react";

class QuoteControl extends Component {
  render() {
    const { onClick, quote } = this.props;
    return (
      <div className="quote-box">
        <button
          className="button"
          style={{
            padding: "15px 32px",
            backgroundColor: "#1DA1F2",
            width: "12%",
            height: "100px",
            fontSize: "italic",
            borderRadius: "50em",
            display: "flex",
            alignItems: "center",
          }}
          id="new-quote"
          onClick={onClick}
        >
          {quote}
        </button>
        {/* <button onClick={this.toggleHidden.bind(this)}></button> */}
      </div>
    );
  }
}

export default QuoteControl;
