import React, { Component } from "react";
// import Quote from "./quote";

class QuoteControl extends Component {
  render() {
    const { onChange } = this.props;
    return (
      <div className="quote-box" style={{ width: "98%" }}>
        <button
          onClick={onChange}
          style={{
            padding: "15px 32px",
            backgroundColor: "#1DA1F2",
            width: "12%",
            height: "100px",
            fontSize: "italic",
            borderRadius: "50em",
          }}
        >
          Click Me
        </button>
        <button onClick>Hide Me</button>
      </div>
    );
  }
}

export default QuoteControl;
