import React, { Component } from "react";
import Quote from "./quote";
import QuoteControl from "./quotecontrol";

class QuoteViewer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeButton: false,
      isHidden: true,
      quote: "",
      author: "",
    };
    this.onShowQuote = this.onShowQuote.bind(this);
    // this.isHidden = this.isHidden.bind(this);
  }
  onShowQuote() {
    this.setState({ activeButton: true });
  }
  Hidden() {
    this.setState({
      isHidden: !this.state.isHidden,
    });
  }
  /* componentDidMount() {
    fetch(API)
      .then(res => res.json())
      .then(data => {
        this.setState({
          quotes: data.quotes
        });
      });
  } */

  render() {
    let quotes;
    if (this.state.activeButton === true) {
      quotes = <Quote />;
    }
    return (
      <div className="quote-box">
        <QuoteControl onChange={this.onShowQuote} />
        <h5>{quotes}</h5>
        <button onClick={this.handleSubmit}>Get new Quote</button>
      </div>
    );
  }
}

export default QuoteViewer;
