import React, { Component } from "react";
import Quote from "./quote";
import QuoteControl from "./quotecontrol";

class QuoteViewer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeButton: false,
      showing: true,
      quote: "",
      author: "",
    };
    this.onShowQuote = this.onShowQuote.bind(this);
    // this.onHideButton = this.onHideButton.bind(this);
  }
  onShowQuote() {
    this.setState({ activeButton: true });
  }
  /* onHideButton() {
    this.setState({ activeButton: true });
  } */

  render() {
    let quotes;
    if (this.state.activeButton === true) {
      quotes = <Quote />;
    }
    return (
      <div className="quote-box">
        <QuoteControl onChange={this.onShowQuote}></QuoteControl>
        <h5 className="title">{quotes}</h5>
      </div>
    );
  }
}

export default QuoteViewer;
